package exam02;

public class CompoundOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num = 125;
		
		num+=50;//num = num + 50;  복합 대입 연산자
		
		System.out.println(num);
		
		num-=50;//num = num - 50;  복합 대입 연산자
		System.out.println(num);

		num*=50;//num = num * 50;  복합 대입 연산자
		System.out.println(num);
		
		num/=50;//num = num / 50;  복합 대입 연산자
		System.out.println(num);
		
		num%=50;//num = num % 50;  복합 대입 연산자
		System.out.println(num);
	}

}
