package exam10;

public class ForBasic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int result = 0;
		
		
		for(int i = 1; i <=100; i++) {
			
			System.out.println("count = " + i);
			result += i;
			System.out.println("result = " + result);
			
			
			
			
		}
		System.out.println("result sum = " + result);
		
	}

}
